mapper.select:
select a.id,a.name,a.content,a.question_type_id,a.creator,a.answer,a.analysis,a.reference referenceName,group_concat(concat(d.field_name,'-',c.point_name)) pointName, e.name questionTypeName, examing_point examingPoint, (select group_concat(m.tag_name,' ') from et_tag m,et_question_2_tag n where m.tag_id=n.tag_id and n.question_id=a.id) tags from et_question a,et_question_2_point b,et_knowledge_point c,et_field d,et_question_type e
a.id=b.question_id and b.point_id=c.point_id and c.field_id=d.field_id and a.question_type_id=e.id
and c.field_id = #{filter.fieldId}
and a.id in(select question_id from et_question_2_tag where tag_id=#{filter.tag})
and c.point_id = #{filter.knowledge}
and a.question_type_id = #{filter.questionType}
and instr(a.name,#{filter.searchParam})<>0
group by a.id,a.name,a.content,a.question_type_id,a.creator,a.answer,a.analysis,a.reference,a.examing_point,e.`name`,a.examing_point,tags order by a.id desc

select a.point_id pointId,a.point_name pointName,a.field_id fieldId,a.memo,a.state,b.field_name fieldName, case when exists(select 1 from et_question_2_point m where m.point_id=a.point_id) then 0 else 1 end removeable from et_knowledge_point a,et_field b
a.field_id=b.field_id
and a.field_id = #{fieldId}

mapper.delete:
		delete from et_question_2_tag where
		
			tag_id not in
			
				#{item}
			
			and
		
		question_id=#{questionId}

mapper.select:
select a.id,a.name,a.content,a.question_type_id,a.creator,a.answer,a.analysis,a.reference referenceName,a.keyword keyword,
a.examing_point examingPoint from et_question a

	a.id = #{questionId}

select a.id questionId,a.expose_times exposeTimes, c.point_id pointId,a.difficulty difficulty,a.question_type_id questionTypeId, a.right_times rightTimes, a.wrong_times wrongTimes,a.points point,a.keyword from et_question a,et_question_2_point b ,et_knowledge_point c
a.id=b.question_id and b.point_id=c.point_id
and c.point_id in #{item}
