mapper.select:
select a.training_id trainingId,a.training_name trainingName,a.training_desc trainingDesc, a.create_time createTime,a.creator creatorId,b.user_name creatorName,a.status,a.state_time stateTime,a.exp_time expTime,a.is_private privatee from et_training a,et_user b where a.creator=b.user_id
and a.creator = #{userId}
order by a.create_time desc,a.state_time desc

select section_id sectionId,section_name sectionName,section_desc sectionDesc,training_id trainingId,
user_id userId,file_name fileName,file_path filePath,file_md5 fileMd5,file_type fileType from et_training_section
where training_id=#{trainingId}

	and user_id=#{userId}

select section_id sectionId,section_name sectionName,section_desc sectionDesc,training_id trainingId,
user_id userId,file_name fileName,file_path filePath,file_md5 fileMd5,file_type fileType from et_training_section
where section_id=#{sectionId}

	and user_id=#{userId}

select b.training_name trainingName,a.section_id sectionId,a.section_name sectionName,a.section_desc sectionDesc,b.training_id trainingId, c.user_id userId,a.file_name fileName,a.file_path filePath,a.file_md5 fileMd5,a.file_type fileType,c.user_name userName, ifnull((select e.process from et_user_training_history e where e.user_id=c.user_id and e.section_id=a.section_id),0) process,m.dep_name from et_training_section a join et_training b on a.training_id=b.training_id join et_user c LEFT JOIN (select d.user_id,e.dep_id,e.dep_name from et_user_2_department d,et_department e where d.dep_id=e.dep_id) m on c.user_id=m.user_id where a.training_id=#{trainingId}
and c.user_id in
#{item}
and 1=2
and (instr(c.true_name,#{searchStr}) <> 0 or instr(c.user_name,#{searchStr}) <> 0 or instr(c.phone_num,#{searchStr}) <> 0 or instr(c.national_id,#{searchStr}) <> 0 or instr(c.email,#{searchStr}) <>0 or instr(m.dep_name,#{searchStr}) <> 0)

select a.training_id trainingId,a.training_name trainingName,a.training_desc trainingDesc, a.create_time createTime,a.creator creatorId,b.user_name creatorName,a.status,a.state_time stateTime,a.exp_time expTime,a.is_private privatee, c.user_id userId,c.user_name userName,c.true_name trueName,c.dep_name depName from et_training a,et_user b,(select p.*,n.dep_name from et_user_2_department m join et_department n on m.dep_id=n.dep_id right join et_user p on m.user_id=p.user_id) c,et_user_2_role d,et_role e where a.creator=b.user_id and c.user_id=d.user_id and d.role_id=e.id and e.authority='ROLE_STUDENT' and a.training_id=#{trainingId}
and a.creator = #{userId}
and (instr(c.true_name,#{searchStr}) <> 0 or instr(c.user_name,#{searchStr}) <> 0 or instr(c.phone_num,#{searchStr}) <> 0 or instr(c.national_id,#{searchStr}) <> 0 or instr(c.email,#{searchStr}) <>0 or instr(c.dep_name,#{searchStr}) <> 0)
order by a.create_time desc,a.state_time desc
