mapper.select:
select a.comment_id commentId,a.refer_id referId,a.index_id indexId, a.user_id userId,b.user_name username,a.content_msg contentMsg,a.quoto_id quotoId, a.re_id reId,a.create_time createTime from et_comment a,et_user b where a.user_id=b.user_id and a.refer_id=#{referId} and a.comment_type=#{commentType}
and a.index_id <= #{indexId}
order by a.create_time desc
