select a.id examId,a.exam_name examName,a.exam_subscribe examSubscribe,a.exam_type examType,a.start_time effTime, a.end_time expTime,a.exam_paper_id examPaperId,a.creator,a.creator_id creatorId,b.approved,(select name from et_exam_paper m where a.exam_paper_id=m.id) examPaperName, b.seri_no seriNo from et_exam a,et_user_exam_history b where a.approved=1 and a.end_time > sysdate() and a.id=b.exam_id and b.user_id=#{userId}
and a.exam_type in
#{item}
order by a.start_time desc
